mapper.insert:
insert into Resume(name, languageId, yn, createTime, updateTime)
values (#{name, jdbcType=VARCHAR}, #{languageId,jdbcType=INTEGER}, 1, now(), now())

  select last_insert_id();

mapper.select:
select * from Resume where 1=1

  and id = #{id}


  and name = #{name}


  and languageId = #{languageId}


  and yn = #{yn}
