mapper.update:
update LanguageAbility set
name = #{name, jdbcType=VARCHAR},
readAndWrite = #{readAndWrite,jdbcType=VARCHAR},
listenAndSpeaking = #{listenAndSpeaking,jdbcType=VARCHAR}
where
id = #{id}

mapper.select:
select
*
from LanguageAbility
where 1 = 1

  and id = #{id}


  and resumeId = #{resumeId}


  and readAndWrite = #{readAndWrite}


  and listenAndSpeaking = #{listenAndSpeaking}
