mapper.select:
select
*
from ProjectExperience
where 1 = 1

  and id = #{id}


  and resumeId = #{resumeId}


  and name = #{name}


  and startDate = #{startDate}


  and endDate = #{endDate}


  and isIt = #{isIt}


  and softwareEnvironment = #{softwareEnvironment}


  and hardwareEnvironment = #{hardwareEnvironment}


  and developTool = #{developTool}


  and responsibility = #{responsibility}


  and projectDescription = #{projectDescription}
