mapper.select:


  and project.id = #{id}


  and project.name like "%"#{name}"%"


  and project.startDate >= #{startDate}


  and #{endDate} >= project.endDate


  and project.status = #{status}


  and company.name like "%"#{company.name}"%"


  and position.name like "%"#{position.name}"%"


  and manager.name like "%"#{manager.name}"%"

order by project.createTime desc
