mapper.insert:
insert into Certificate(
resumeId,
name,
acquireDate
)
values (
#{resumeId},
#{name, jdbcType=VARCHAR},
#{acquireDate,jdbcType=VARCHAR}
)

  select last_insert_id();

mapper.update:
update Certificate set
  name = #{name, jdbcType=VARCHAR},
  acquireDate = #{acquireDate,jdbcType=VARCHAR}
where
  id = #{id}

mapper.select:
select
*
from Certificate
where 1 = 1

  and id = #{id}


  and resumeId = #{resumeId}


  and name = #{name}


  and acquireDate = #{acquireDate}
