mapper.update:
update Resume set name = #{name}, sex = #{sex}, telephone = #{telephone}, age = #{age}, email = #{email}, education = #{education}, workYears = #{workYears}, marital = #{marital}, location = #{location}, industry = #{industry}, company = #{company}, position = #{position}, salary = #{salary}, selfEvaluation = #{selfEvaluation}, other = #{other}, keyword = #{keyword},
originalResumeName = #{originalResumeName}, originalResumeUri = #{originalResumeUri}, originalResumeText = #{originalResumeText},
language = #{language}, updateTime = now(), updateUser = #{updateUser} where id = #{id}

mapper.select:
select * from Resume  where 1 = 1

    and id = #{id}


    and name = #{name}


    and sex = #{sex}


    and keyword = #{keyword}


    and education = #{education}


    and position = #{position}


    and yn = #{yn}
