mapper.select:
select * from TempCandidate  where 1 = 1

  and id = #{id}


  and name like "%"#{name}"%"


  and companyId = #{companyId}


  and positionId = #{positionId}


  and cityId = #{cityId}


  and projectCompanyId = #{projectCompanyId}


  and projectId = #{projectId}


  and addUserId = #{addUserId}


  and searchStatusId = #{searchStatusId}


  and yn = #{yn}
